import React, { FC, FormEvent, useState, ChangeEvent } from 'react';

type SignupDate = {
  name: string,
  email: string,
  password: string,
}

const SignupComponents:FC = () => {
  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    error: false,
    loading: false,
    message: '',
    showForm: true,
  });

  const {
    name,
    email,
    password,
    error,
    loading,
    message,
    showForm,
  } = values;

  const handleSubmit = (e:FormEvent<HTMLElement>) => {
    e.preventDefault();
    console.table({ name, email, password, error, loading, message, showForm });
  };

  const handleChange = (inputName: string) => (e:ChangeEvent<HTMLInputElement>) => {
    setValues({ ...values, error: false, [inputName]: e.target.value });
  };

  const signupForm:FC = () => {
    return (
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input type="text" value={name} onChange={handleChange('name')} className="form-control" placeholder="Type your name" />
        </div>

        <div className="form-group">
          <input type="email" value={email} onChange={handleChange('email')} className="form-control" placeholder="Type your email" />
        </div>

        <div className="form-group">
          <input type="password" value={password} onChange={handleChange('password')} className="form-control" placeholder="Type your password" />
        </div>

        <div>
          <button type="submit" className="btn btn-primary">
            Signup
          </button>
        </div>
      </form>
    );
  };
  return (
    <>
      { signupForm() }
    </>
  );
};

export default SignupComponents;
