import React, { FC, FormEvent, useState } from 'react';

type SignupDate = {
  name: string,
  email: string,
  password: string,
}

const SignupComponents:FC = () => {
  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    error: '',
    loading: false,
    message: '',
    showForm: true,
  });
  
  const handleSubmit = (e:FormEvent<HTMLElement>) => {
    e.preventDefault();
    console.log('handle submit');
  };

  const handleChange = (e:FormEvent<HTMLElement>) => {
    console.log('handle change', e.target.value);
  };

  const signupForm:FC = () => {
    return (
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input type="text" onChange={handleChange} className="form-control" placeholder="Type your name" />
        </div>

        <div className="form-group">
          <input type="email" onChange={handleChange} className="form-control" placeholder="Type your email" />
        </div>

        <div className="form-group">
          <input type="password" onChange={handleChange} className="form-control" placeholder="Type your password" />
        </div>

        <div>
          <button type="submit" className="btn btn-primary">
            Signup
          </button>
        </div>
      </form>
    );
  };
  return (
    <>
      { signupForm() }
    </>
  );
};

export default SignupComponents;
